<?php defined('BASEPATH') OR exit('No direct script access allowed');class Addon_ad_list extends CI_Controller{	public function __construct(){		parent::__construct();         $this->load->library('session');               $this->load->model('Common_model','dg_model');                $this->load->library('pagination');               mdsession_check();		}        public function view($page=false){                 $page1 = $this->uri->segment('5');                $page1 = !empty($page1) ? $page1 : false;                $parentid = $this->uri->segment('4');                $data['parentdetail'] = $this->dg_model->getSingle('users',array('md5(id)'=>$parentid),'*');		$data['title'] = 'AD List';                $start = $page1;                $limit = 1;                $base_url = (ADMINURL.'bp/addon_ad_list/view/'.$parentid);                $countItem = $this->dg_model->countitem('users',array('user_type'=>'AD','md5(parentid)'=>$parentid));                $data["pagination"] = pagination($base_url, $countItem, $limit);                                $data['mduser']  = $this->dg_model->getfilter('users',array('user_type'=>'AD','md5(parentid)'=>$parentid),$limit,$start, 'id DESC');                $data['parentid'] = $parentid;                $data['total'] = $countItem;		mdview('list-addon-ad',$data);	}}