<?php defined('BASEPATH') OR exit('No direct script access allowed');class Addon_ag_list extends CI_Controller{	public function __construct(){		parent::__construct();        $this->load->library('session');                $this->load->model('Common_model','dg_model');                $this->load->library('pagination');               mdsession_check();		}        public function view($page=false){                  $page1 = $this->uri->segment('5');                $page1 = !empty($page1) ? $page1 : false;                $parentid = $this->uri->segment('4');                                $data['parentdetail'] = $this->dg_model->getSingle( 'users',array('md5(id)'=>$parentid),'*');		$data['title'] = 'Retailer List';                $start = $page1;                $limit = 1;                $base_url = (ADMINURL.'bp/addon_ag_list/view/'.$parentid);                $countItem = $this->dg_model->countitem('users',array('user_type'=>'Retailer','md5(parentid)'=>$parentid));                $data["pagination"] = pagination($base_url, $countItem, $limit);                $data['aguser']     = $this->dg_model->getfilter('users',array('user_type'=>'Retailer','md5(parentid)'=>$parentid),$limit,$start, 'id DESC');                $data['total']      = $countItem;		$data['parentid']   = $parentid;                mdview('list-addon-ag',$data);	}}